import React, { useState, useEffect } from 'react';
import { Link, useParams } from 'react-router-dom';
import { supabase } from '../client';
import { formatTimestamp } from '../utils'; // Import the formatTimestamp function

const ViewPost = () => {
    const { id } = useParams();
    const [post, setPost] = useState(null);
    const [comments, setComments] = useState([]);
    const [newComment, setNewComment] = useState('');

    useEffect(() => {
        fetchPost();
        fetchComments();
    }, []);

    const fetchPost = async () => {
        const { data, error } = await supabase
            .from('Posts')
            .select('*')
            .eq('id', id)
            .single();

        if (error) {
            console.error('Error fetching post:', error.message);
        } else {
            // Format the created_at timestamp to relative time
            const formattedCreatedAt = formatTimestamp(data.created_at);
            // Update the post data with the formatted relative time
            setPost({ ...data, created_at: formattedCreatedAt });
        }
    };

    const fetchComments = async () => {
        try {
            const { data, error } = await supabase
                .from('Posts')
                .select('comments')
                .eq('id', id)
                .single();
    
            if (error) {
                console.error('Error fetching comments:', error.message);
            } else {
                // Ensure comments data is an array
                let commentsArray = [];
                if (typeof data.comments === 'string') {
                    // Parse the string to convert it into an array
                    commentsArray = JSON.parse(data.comments);
                } else if (Array.isArray(data.comments)) {
                    // If already an array, use it directly
                    commentsArray = data.comments;
                } else {
                    console.error('Comments data is not an array:', data.comments);
                }
    
                // Set the comments state
                setComments(commentsArray);
            }
        } catch (error) {
            console.error('Error fetching comments:', error.message);
        }
    };

    const handleChange = (event) => {
        setNewComment(event.target.value);
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
    
        if (!newComment.trim()) return;
    
        // Combine new comment with existing comments
        const updatedComments = [...comments, newComment];
    
        await supabase.from('Posts').update({ comments: updatedComments }).eq('id', id);
    
        setComments(updatedComments);
        setNewComment('');
    };

    const handleUpvote = async () => {
        try {
            // Increment the upvotes count by one
            const updatedUpvotes = post.upvotes + 1;
    
            // Update the upvotes count in the database
            await supabase.from('Posts').update({ upvotes: updatedUpvotes }).eq('id', id);
    
            // Update the local state with the new upvotes count
            setPost(prevPost => ({ ...prevPost, upvotes: updatedUpvotes }));
        } catch (error) {
            console.error('Error upvoting post:', error.message);
        }
    };

    return (
        <div className="ViewPost">
            {post ? (
                <div>
                    <p>Created at: {post.created_at}</p>
                    <h1>{post.title}</h1>
                    <p>{post.content}</p>
                    {post.imageURL && <img src={post.imageURL} alt="Post" />}
                    <p>{post.upvotes} upvotes</p>
                    <button onClick={handleUpvote}>👍</button>
                    <Link to={`/edit/${id}`}>Edit</Link>
                    <form onSubmit={handleSubmit}>
                        <textarea
                            rows="3"
                            cols="50"
                            value={newComment}
                            onChange={handleChange}
                            placeholder="Add a comment..."
                        ></textarea>
                        <br />
                        <button type="submit">Submit Comment</button>
                    </form>
                    <div>
                        <h2>Comments:</h2>
                        {comments.length > 0 ? (
                            <ul>
                                {comments.map((comment, index) => (
                                    <li key={index}>{comment}</li>
                                ))}
                            </ul>
                        ) : (
                            <p>No comments yet.</p>
                        )}
                    </div>
                </div>
            ) : (
                <p>Loading...</p>
            )}
        </div>
    );
};

export default ViewPost;
