import React, { useState, useEffect } from 'react';
import { Link, useParams } from 'react-router-dom';
import { supabase } from '../client';

const ViewPost = () => {
    const { id } = useParams();
    const [post, setPost] = useState(null);
    const [comments, setComments] = useState([]);
    const [newComment, setNewComment] = useState('');

    useEffect(() => {
        fetchPost();
        fetchComments();
    }, []);

    const fetchPost = async () => {
        const { data, error } = await supabase
          .from('Posts')
          .select('*')
          .eq('id', id)
          .single();

        if (error) {
          console.error('Error fetching post:', error.message);
        } else {
          setPost(data);
        }
    };

    const fetchComments = async () => {
        const { data, error } = await supabase
            .from('Posts')
            .select('comments')
            .eq('id', id)
            .single();
    
        if (error) {
            console.error('Error fetching comments:', error.message);
        } else {
            if (Array.isArray(data.comments)) {
                // Remove empty strings and trim whitespace from comments
                const cleanedComments = data.comments.filter(comment => comment.trim() !== '').map(comment => comment.trim());
                setComments(cleanedComments);
            } else {
                console.error('Comments data is not an array:', data.comments);
            }
        }
    };
    
    const handleChange = (event) => {
        setNewComment(event.target.value);
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
    
        if (!newComment.trim()) return;
    
        // Combine new comment with existing comments
        const updatedComments = [...comments, newComment];
    
        await supabase.from('Posts').update({ comments: updatedComments }).eq('id', id);
    
        setComments(updatedComments);
        setNewComment('');
    };

    return (
        <div className="ViewPost">
            {post ? (
                <div>
                    <p>Created at: {post.created_at}</p>
                    <h1>{post.title}</h1>
                    <p>{post.content}</p>
                    {post.imageURL && <img src={post.imageURL} alt="Post" />}
                    <p>Upvotes: {post.upvotes}</p>
                    <Link to={`/edit/${id}`}>Edit</Link>
                    <form onSubmit={handleSubmit}>
                        <textarea
                            rows="3"
                            cols="50"
                            value={newComment}
                            onChange={handleChange}
                            placeholder="Add a comment..."
                        ></textarea>
                        <br />
                        <button type="submit">Submit</button>
                    </form>
                    <div>
                        <h2>Comments:</h2>
                        {comments.length > 0 ? (
                            <ul>
                                {comments.map((comment, index) => (
                                    <li key={index}>{comment}</li>
                                ))}
                            </ul>
                        ) : (
                            <p>No comments yet.</p>
                        )}
                    </div>
                </div>
            ) : (
                <p>Loading...</p>
            )}
        </div>
    );
};

export default ViewPost;
