import React, { useState, useEffect } from 'react';
import { supabase } from '../client';
import { formatDistanceToNow } from 'date-fns'; // Import the formatDistanceToNow function
import Card from '../components/Card';

const ReadPosts = () => {
    const [posts, setPosts] = useState([]);
    const [searchQuery, setSearchQuery] = useState('');
    const [sortBy, setSortBy] = useState(null);

    useEffect(() => {
        fetchPosts();
    }, [searchQuery, sortBy]);

    const fetchPosts = async () => {
        let { data, error } = await supabase.from('Posts').select('*');
        if (error) {
            console.error('Error fetching posts:', error.message);
        } else {
            // Filter posts based on search query
            if (searchQuery.trim() !== '') {
                data = data.filter(post =>
                    post.title.toLowerCase().includes(searchQuery.toLowerCase())
                );
            }
            // Sort posts based on sortBy option
            if (sortBy === 'created_at') {
                data = data.sort((a, b) => new Date(b.created_at) - new Date(a.created_at));
            } else if (sortBy === 'upvotes') {
                data = data.sort((a, b) => b.upvotes - a.upvotes);
            }
            // Format the created_at timestamps
            data = data.map(post => ({
                ...post,
                created_at: formatDistanceToNow(new Date(post.created_at), { addSuffix: true })
            }));
            setPosts(data);
        }
    };

    const handleSearchChange = (event) => {
        setSearchQuery(event.target.value);
    };

    const handleSortBy = (sortByOption) => {
        setSortBy(sortByOption);
    };

    return (
        <div className="ReadPosts">
            <input
                type="text"
                placeholder="Search by title..."
                value={searchQuery}
                onChange={handleSearchChange}
            />
            <div>
                <button onClick={() => handleSortBy('created_at')}>Sort by Created Time</button>
                <button onClick={() => handleSortBy('upvotes')}>Sort by Upvotes</button>
            </div>
            {posts.length > 0 ? (
                posts.map((post) => <Card key={post.id} {...post} />)
            ) : (
                <h2>No Posts Found</h2>
            )}
        </div>
    );
};

export default ReadPosts;
