import React, { useState } from 'react';
import { supabase } from '../client'
import './CreatePost.css'

const CreatePost = () => {

    const [post, setPost] = useState({title: "", content: "", imageURL: ""})

    const handleChange = (event) => {
        const {name, value} = event.target;
        setPost( (prev) => {
            return {
                ...prev,
                [name]:value,
            }
        })
    }

    const createPost = async (event) => {
        event.preventDefault();

        await supabase
          .from('Posts')
          .insert({title: post.title, content: post.content, imageURL: post.imageURL})
          .select();

        window.location = "/";
      }

    return (
        <div>
            <form>
                <label for="title">Title</label> <br />
                <input type="text" id="title" name="title" onChange={handleChange} /><br />
                <br/>

                <label for="content">Content (Optional)</label><br />
                <textarea rows="5" cols="50" id="imageURL" name="description" onChange={handleChange}>
                <input type="text" id="content" name="content" onChange={handleChange} /><br />
                <br/>

                <label for="imageURL">Image URL (Optional)</label><br />
                <input type="text" id="imageURL" name="imageURL" onChange={handleChange} /><br />
                </textarea>
                <br/>
                <input type="submit" value="Create Post" onClick={createPost} />
            </form>
        </div>
    )
}

export default CreatePost
