import React, { useState } from 'react';
import { supabase } from '../client'
import { useParams } from 'react-router-dom';
import './EditPost.css'

const EditPost = ({ data }) => {

    const { id } = useParams();
    const [post, setPost] = useState({ id: null, title: "", content: "", imageURL: "" });

    const handleChange = (event) => {
        const { name, value } = event.target;
        setPost((prev) => {
            return {
                ...prev,
                [name]: value,
            }
        })
    }

    // UPDATE post
    const updatePost = async (event) => {
        event.preventDefault();

        await supabase
            .from('Posts')
            .update({ title: post.title, content: post.content, imageURL: post.imageURL })
            .eq('id', id);

        window.location = "/";
    }

    // UPDATE post
    const deletePost = async (event) => {
        event.preventDefault();

        await supabase
            .from('Posts')
            .delete()
            .eq('id', id);

        window.location = "http://localhost:3000/";
    }

    return (
        <div>
            <form>
            <input type="text" id="title" name="title" placeholder="Title" onChange={handleChange} /><br />
                <br/>

                <textarea rows="5" cols="50" id="content" name="content" placeholder="Content (Optional)" onChange={handleChange}>
                </textarea>
                <br/>

                <input type="text" id="imageURL" name="imageURL" placeholder="Image URL (Optional)" onChange={handleChange} /><br />
                <br/>

                <input type="submit" value="Update Post" onClick={updatePost} />
                <button className="deleteButton" onClick={deletePost}>Delete Post</button>
            </form>
        </div>
    )
}

export default EditPost
